const t=JSON.parse(`{"key":"v-7cd59d6d","path":"/studynotes/database/mysql/log/undo%20log%EF%BC%9A%E4%B8%96%E4%B8%8A%E7%9C%9F%E6%9C%89%E5%90%8E%E6%82%94%E8%8D%AF.html","title":"undo log：世上真有后悔药","lang":"zh-CN","frontmatter":{"title":"undo log：世上真有后悔药","date":"2023-03-16T00:00:00.000Z","order":5,"icon":"write","category":["数据库"],"tag":["MySQL"],"sticky":false,"star":false,"article":true,"timeline":true,"description":"本文内容 1. 为什么需要 undo log？ InnoDB 存储引擎是支持事务的，而事务有一个非常重要的特征是 原子性，即事务中的所有操作，要么全部完成，要么全部失败。 但是，在实际运行过程中，就会有出现某个 事务执行到一半 的情况，例如： 事务执行过程中 出现错误，如实例崩溃、突然断电宕机等； 开发人员在事务执行过程中手动执行 ROLLBACK 语句结束当前事务。 还有 两阶段提交时，在某时刻崩溃后，如果 binlog 还未刷盘，则要回滚这个事务。","head":[["meta",{"property":"og:url","content":"https://aruni.me/docs/studynotes/database/mysql/log/undo%20log%EF%BC%9A%E4%B8%96%E4%B8%8A%E7%9C%9F%E6%9C%89%E5%90%8E%E6%82%94%E8%8D%AF.html"}],["meta",{"property":"og:site_name","content":"AruNi's domain"}],["meta",{"property":"og:title","content":"undo log：世上真有后悔药"}],["meta",{"property":"og:description","content":"本文内容 1. 为什么需要 undo log？ InnoDB 存储引擎是支持事务的，而事务有一个非常重要的特征是 原子性，即事务中的所有操作，要么全部完成，要么全部失败。 但是，在实际运行过程中，就会有出现某个 事务执行到一半 的情况，例如： 事务执行过程中 出现错误，如实例崩溃、突然断电宕机等； 开发人员在事务执行过程中手动执行 ROLLBACK 语句结束当前事务。 还有 两阶段提交时，在某时刻崩溃后，如果 binlog 还未刷盘，则要回滚这个事务。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-06-10T13:25:13.000Z"}],["meta",{"property":"article:author","content":"AruNi_Lu"}],["meta",{"property":"article:tag","content":"MySQL"}],["meta",{"property":"article:published_time","content":"2023-03-16T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2023-06-10T13:25:13.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"undo log：世上真有后悔药\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2023-03-16T00:00:00.000Z\\",\\"dateModified\\":\\"2023-06-10T13:25:13.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"AruNi_Lu\\",\\"url\\":\\"https://github.com/AruNi-01\\"}]}"]]},"headers":[{"level":2,"title":"1. 为什么需要 undo log？","slug":"_1-为什么需要-undo-log","link":"#_1-为什么需要-undo-log","children":[]},{"level":2,"title":"2. 什么是 undo log？","slug":"_2-什么是-undo-log","link":"#_2-什么是-undo-log","children":[]},{"level":2,"title":"3. undo log 何时刷盘？","slug":"_3-undo-log-何时刷盘","link":"#_3-undo-log-何时刷盘","children":[]},{"level":2,"title":"4. 总结","slug":"_4-总结","link":"#_4-总结","children":[]},{"level":2,"title":"5. 参考文章","slug":"_5-参考文章","link":"#_5-参考文章","children":[]}],"git":{"createdTime":1678969582000,"updatedTime":1686403513000,"contributors":[{"name":"AruNi-01","email":"1298911600@qq.com","commits":1},{"name":"aarynlu","email":"aarynlu@tencent.com","commits":1}]},"readingTime":{"minutes":3.79,"words":1137},"filePathRelative":"studynotes/database/mysql/log/undo log：世上真有后悔药.md","localizedDate":"2023年3月16日","excerpt":"<details class=\\"hint-container details\\"><summary>本文内容</summary>\\n\\n</details>\\n<h2> 1. 为什么需要 undo log？</h2>\\n<p>InnoDB 存储引擎是支持事务的，而事务有一个非常重要的特征是 <strong>原子性</strong>，即事务中的所有操作，要么全部完成，要么全部失败。</p>\\n<p>但是，在实际运行过程中，就会有出现某个 <strong>事务执行到一半</strong> 的情况，例如：</p>\\n<ul>\\n<li>\\n<p>事务执行过程中 <strong>出现错误</strong>，如实例崩溃、突然断电宕机等；</p>\\n</li>\\n<li>\\n<p>开发人员在事务执行过程中手动执行 <strong>ROLLBACK</strong> 语句结束当前事务。</p>\\n</li>\\n<li>\\n<p>还有 <strong>两阶段提交时，在某时刻崩溃后，如果 binlog 还未刷盘，则要回滚这个事务</strong>。</p>\\n</li>\\n</ul>","autoDesc":true}`);export{t as data};
