const t=JSON.parse('{"key":"v-3b4d20f6","path":"/studynotes/cs/os/net_system/%E9%98%BB%E5%A1%9E%E9%9D%9E%E9%98%BB%E5%A1%9E%E5%90%8C%E6%AD%A5%E5%BC%82%E6%AD%A5IO.html","title":"阻塞非阻塞同步异步 IO","lang":"zh-CN","frontmatter":{"title":"阻塞非阻塞同步异步 IO","date":"2023-08-06T00:00:00.000Z","order":1,"icon":"write","category":["计算机基础"],"tag":["操作系统"],"sticky":false,"star":false,"article":true,"timeline":true,"description":"本文内容 1. IO 是什么？ IO 全称是 Input/Output，即输入输出，常见的 IO 有磁盘 IO，网路 IO 等。 当我们需要将内存中的数据持久化时，就需要向磁盘写数据，这个过程就是一次输出（从内存角度来说，将内存中的数据输出到磁盘，从磁盘角度来说则是输入了）；反之，我们需要把数据从磁盘中取出时，就是一个输入的过程。这时磁盘就是一个 IO 设备，负责将数据具体的写入和读出。 当我们需要跨主机进行通信时，一个常用的方法就是使用网络通信，而网络通信过程中需要借助网络 IO 设备，比如网卡、路由器、交换机等，它们负责数据流的输入和输出。","head":[["meta",{"property":"og:url","content":"https://aruni.me/studynotes/cs/os/net_system/%E9%98%BB%E5%A1%9E%E9%9D%9E%E9%98%BB%E5%A1%9E%E5%90%8C%E6%AD%A5%E5%BC%82%E6%AD%A5IO.html"}],["meta",{"property":"og:site_name","content":"AruNi"}],["meta",{"property":"og:title","content":"阻塞非阻塞同步异步 IO"}],["meta",{"property":"og:description","content":"本文内容 1. IO 是什么？ IO 全称是 Input/Output，即输入输出，常见的 IO 有磁盘 IO，网路 IO 等。 当我们需要将内存中的数据持久化时，就需要向磁盘写数据，这个过程就是一次输出（从内存角度来说，将内存中的数据输出到磁盘，从磁盘角度来说则是输入了）；反之，我们需要把数据从磁盘中取出时，就是一个输入的过程。这时磁盘就是一个 IO 设备，负责将数据具体的写入和读出。 当我们需要跨主机进行通信时，一个常用的方法就是使用网络通信，而网络通信过程中需要借助网络 IO 设备，比如网卡、路由器、交换机等，它们负责数据流的输入和输出。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-08-06T16:27:18.000Z"}],["meta",{"property":"article:author","content":"AruNi_Lu"}],["meta",{"property":"article:tag","content":"操作系统"}],["meta",{"property":"article:published_time","content":"2023-08-06T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2023-08-06T16:27:18.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"阻塞非阻塞同步异步 IO\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2023-08-06T00:00:00.000Z\\",\\"dateModified\\":\\"2023-08-06T16:27:18.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"AruNi_Lu\\",\\"url\\":\\"https://github.com/AruNi-01\\"}]}"]]},"headers":[{"level":2,"title":"1. IO 是什么？","slug":"_1-io-是什么","link":"#_1-io-是什么","children":[]},{"level":2,"title":"2. 阻塞和非阻塞 IO","slug":"_2-阻塞和非阻塞-io","link":"#_2-阻塞和非阻塞-io","children":[{"level":3,"title":"2.1 阻塞 IO","slug":"_2-1-阻塞-io","link":"#_2-1-阻塞-io","children":[]},{"level":3,"title":"2.2 非阻塞 IO","slug":"_2-2-非阻塞-io","link":"#_2-2-非阻塞-io","children":[]}]},{"level":2,"title":"3. 同步和异步 IO","slug":"_3-同步和异步-io","link":"#_3-同步和异步-io","children":[]},{"level":2,"title":"4. 总结","slug":"_4-总结","link":"#_4-总结","children":[]}],"git":{"createdTime":1691339238000,"updatedTime":1691339238000,"contributors":[{"name":"aarynlu","email":"aarynlu@tencent.com","commits":1}]},"readingTime":{"minutes":4.94,"words":1481},"filePathRelative":"studynotes/cs/os/net_system/阻塞非阻塞同步异步IO.md","localizedDate":"2023年8月6日","excerpt":"<details class=\\"hint-container details\\"><summary>本文内容</summary>\\n\\n</details>\\n<h2> 1. IO 是什么？</h2>\\n<p>IO 全称是 Input/Output，即输入输出，常见的 IO 有磁盘 IO，网路 IO 等。</p>\\n<p>当我们需要将内存中的数据持久化时，就需要向磁盘写数据，这个过程就是一次输出（从内存角度来说，将内存中的数据输出到磁盘，从磁盘角度来说则是输入了）；反之，我们需要把数据从磁盘中取出时，就是一个输入的过程。这时磁盘就是一个 IO 设备，负责将数据具体的写入和读出。</p>\\n<p>当我们需要跨主机进行通信时，一个常用的方法就是使用网络通信，而网络通信过程中需要借助网络 IO 设备，比如网卡、路由器、交换机等，它们负责数据流的输入和输出。</p>","autoDesc":true}');export{t as data};
